    private void AddBTNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AddBTNActionPerformed
      double A1 = Double.parseDouble(a1.getText());
      double A2 = Double.parseDouble(a2.getText());
      double B1 = Double.parseDouble(b1.getText());
      double B2 = Double.parseDouble(b2.getText());
       
      double C1 = Double.parseDouble(c1.getText());
      double C2 = Double.parseDouble(c2.getText());
      double D1 = Double.parseDouble(d1.getText());
      double D2 = Double.parseDouble(d2.getText());
        
      double E1 = A1+C1;
      double E2 = A2+C2;
      double F1 = B1+D1;
      double F2 = B2+D2;
      
      e1.setText(""+E1);
      e2.setText(""+E2);
      f1.setText(""+F1);
      f2.setText(""+F2);
      
      SignTF.setText("+");
        
    }//GEN-LAST:event_AddBTNActionPerformed

    private void SubtractBTNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SubtractBTNActionPerformed
     double A1 = Double.parseDouble(a1.getText());
      double A2 = Double.parseDouble(a2.getText());
      double B1 = Double.parseDouble(b1.getText());
      double B2 = Double.parseDouble(b2.getText());
       
      double C1 = Double.parseDouble(c1.getText());
      double C2 = Double.parseDouble(c2.getText());
      double D1 = Double.parseDouble(d1.getText());
      double D2 = Double.parseDouble(d2.getText());
        
      
      
      double E1 = A1-C1;
      double E2 = A2-C2;
      double F1 = B1-D1;
      double F2 = B2-D2;
      
      e1.setText(""+E1);
      e2.setText(""+E2);
      f1.setText(""+F1);
      f2.setText(""+F2);
      
      SignTF.setText("-");
        
        
        
    }//GEN-LAST:event_SubtractBTNActionPerformed

    private void multiplyBTNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_multiplyBTNActionPerformed
      double A1 = Double.parseDouble(a1.getText());
      double A2 = Double.parseDouble(a2.getText());
      double B1 = Double.parseDouble(b1.getText());
      double B2 = Double.parseDouble(b2.getText());
       
      double C1 = Double.parseDouble(c1.getText());
      double C2 = Double.parseDouble(c2.getText());
      double D1 = Double.parseDouble(d1.getText());
      double D2 = Double.parseDouble(d2.getText());
        
      
      
      double E1 = (A1*C1)+(A2*D1);
      double E2 = (A1*C2)+(A2*D2);
      double F1 = (B1*C1)+(B2*D1);
      double F2 = (B1*C2)+(B2*D2);
      
      e1.setText(""+E1);
      e2.setText(""+E2);
      f1.setText(""+F1);
      f2.setText(""+F2);
      
      SignTF.setText("*");
        
        
    }//GEN-LAST:event_multiplyBTNActionPerformed

    private void a1KeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_a1KeyTyped
char ch = evt.getKeyChar();
if((ch>='a'&& ch<='z')||(ch>='A'&& ch<='Z')) {
            evt.consume();
        }
    }//GEN-LAST:event_a1KeyTyped

    private void a1FocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_a1FocusLost
    double val = Double.parseDouble(a1.getText());
   if((val>=1000)||(val<=-1000))
   {
       JOptionPane.showMessageDialog(null,"Digit should be the range of -999 to 999. ");
       ClearBTN.doClick();
      
   }
    }//GEN-LAST:event_a1FocusLost

