    private void d2FocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_d2FocusLost
       double val = Double.parseDouble(d2.getText());
   if((val>=1000)||(val<=-1000))
   {
       JOptionPane.showMessageDialog(null,"Digit should be the range of -999 to 999. ");
       ClearBTN.doClick();
      
   }
    }//GEN-LAST:event_d2FocusLost

